
var fs = require('fs');

/**
 * Fetches a file from the disk. Designed to mimic the shape of request/browser-request so
 * the system and wormhole classes repository can be run locally. 
 * 
 */

var request = function (url, callback) {

    var file = fs.readFileSync(url, "utf8");
    
    callback(null, null, file);
}

module.exports = request;