
module.exports = Node;

function Node(system, methodType, methodData) {
    this.id = system.id;
    this.system = system;
    this.methodType = methodType;
    this.methodData = methodData;
}
Node.prototype.copy = function () {
    var out = new Node();
    out.id = this.id;
    out.system = this.system;
    out.methodType = this.methodType;
    out.methodData = this.methodData;
    return out;
}