
var system = require('./system/system.js'),
    wormhole = require('./wormhole/wormhole.js'),
    bridge = require('./bridge/bridge.js'),
    signature = require('./signature/signature.js');

var base = require('./base.js');

var pathfinder = require('local/pathfinder');

var sysRep = require('./system'),
    wrmRep = require('./wormhole'),
    whcRep = require('./wormhole-class'),
    sigRep = require('./signature'),
    usrRep = require('./user');

// Inserts relevant repositories into all of the model objects.

bridge.attachRepositories(sysRep);
system.attachRepositories(sysRep, wrmRep, sigRep);
wormhole.attachRepositories(sysRep, wrmRep, whcRep, usrRep);
signature.attachRepositories(usrRep);

system.attachPathfinder(pathfinder);
wormhole.attachPathfinder(pathfinder);


module.exports = base;