'use strict';
var baseRepository = require('../base');


var base = baseRepository({
    path: 'metadata',
    makeRemote: function (x) { return x.value === undefined ? null : x.value;},
    idFromLocal: function (x) { return x.key; },
});

var me = {
    get: base.get,
    initialize: base.initialize,
    changed: base.changed,
    changedRemoveHandler: base.changedRemoveHandler
};
me.set = function (key, value) {            
    base.update({ key: key, value: value });
}

module.exports = me;


